export const URL = "";
export const getApi = (path, params) => fetch(`${URL}/${path}?${params}`).then(res => res.json).catch(err => Promise.reject(err));
export const postApi = (path, data) => fetch(`${URL}/${path}`, {
  method: "POST",
  body: data
}).then(res => res.json()).catch(err => Promise.reject(err));
